classdef non_ribosomal_protein
	% This file was automatically generated by OneModel.
	% Any changes you make to it will be overwritten the next time
	% the file is generated.

	properties
		p      % Default model parameters.
		x0     % Default initial conditions.
		M      % Mass matrix for DAE systems.
		opts   % Simulation options.
	end

	methods
		function obj = non_ribosomal_protein()
			%% Constructor of non_ribosomal_protein.
			obj.p    = obj.default_parameters();
			obj.x0   = obj.initial_conditions();
			obj.M    = obj.mass_matrix();
			obj.opts = obj.simulation_options();
		end

		function p = default_parameters(~)
			%% Default parameters value.
			p = [];
			p.no_ribo__N = 1735.0;
			p.no_ribo__omega = 0.0361;
			p.no_ribo__d_m = 0.2;
			p.no_ribo__k_b = 12.4404;
			p.no_ribo__k_u = 10.0454;
			p.no_ribo__l_p = 333.0;
			p.no_ribo__l_e = 25.0;
		end

		function x0 = initial_conditions(~)
			%% Default initial conditions.
			x0 = [
				100.0 % no_ribo__m
			];
		end

		function M = mass_matrix(~)
			%% Mass matrix for DAE systems.
			M = [
				1 
			];
		end

		function opts = simulation_options(~)
			%% Default simulation options.
			opts.t_end = 10.0;
			opts.t_init = 0.0;
		end

		function dx = ode(~,t,x,p)
			%% Evaluate the ODE.
			%
			% Args:
			%	 t Current time in the simulation.
			%	 x Array with the state value.
			%	 p Struct with the parameters.
			%
			% Return:
			%	 dx Array with the ODE.

			% ODE and algebraic states:
			no_ribo__m = x(1,:);

			% Assigment states:
			no_ribo__nu = 1;
			no_ribo__mu = 1;
			no_ribo__r = 1;
			no_ribo__m_h = 1;
			no_ribo__J_host_sum = 1;
			no_ribo__K_C0 = p.no_ribo__k_b./(p.no_ribo__k_u + no_ribo__nu./p.no_ribo__l_e);
			no_ribo__E_m = 6.3492;
			no_ribo__J = no_ribo__E_m.*p.no_ribo__omega./(p.no_ribo__d_m./no_ribo__K_C0 + no_ribo__mu.*no_ribo__r);

			% der(no_ribo__m)
			dx(1,1) = (no_ribo__m_h.*p.no_ribo__N.*no_ribo__J./no_ribo__J_host_sum - no_ribo__m).*no_ribo__mu;

		end
		function out = simout2struct(~,t,x,p)
			%% Convert the simulation output into an easy-to-use struct.

			% We need to transpose state matrix.
			x = x';
			% ODE and algebraic states:
			no_ribo__m = x(1,:);

			% Assigment states:
			no_ribo__nu = 1;
			no_ribo__mu = 1;
			no_ribo__r = 1;
			no_ribo__m_h = 1;
			no_ribo__J_host_sum = 1;
			no_ribo__K_C0 = p.no_ribo__k_b./(p.no_ribo__k_u + no_ribo__nu./p.no_ribo__l_e);
			no_ribo__E_m = 6.3492;
			no_ribo__J = no_ribo__E_m.*p.no_ribo__omega./(p.no_ribo__d_m./no_ribo__K_C0 + no_ribo__mu.*no_ribo__r);

			% Save simulation time.
			out.t = t;

			% Vector for extending single-value states and parameters.
			ones_t = ones(size(t'));


			% Save states.
			out.no_ribo__nu = no_ribo__nu.*ones_t;
			out.no_ribo__mu = no_ribo__mu.*ones_t;
			out.no_ribo__r = no_ribo__r.*ones_t;
			out.no_ribo__m_h = no_ribo__m_h.*ones_t;
			out.no_ribo__J_host_sum = no_ribo__J_host_sum.*ones_t;
			out.no_ribo__K_C0 = no_ribo__K_C0.*ones_t;
			out.no_ribo__E_m = no_ribo__E_m.*ones_t;
			out.no_ribo__J = no_ribo__J.*ones_t;
			out.no_ribo__m = no_ribo__m.*ones_t;

			% Save parameters.
			out.no_ribo__N = p.no_ribo__N.*ones_t;
			out.no_ribo__omega = p.no_ribo__omega.*ones_t;
			out.no_ribo__d_m = p.no_ribo__d_m.*ones_t;
			out.no_ribo__k_b = p.no_ribo__k_b.*ones_t;
			out.no_ribo__k_u = p.no_ribo__k_u.*ones_t;
			out.no_ribo__l_p = p.no_ribo__l_p.*ones_t;
			out.no_ribo__l_e = p.no_ribo__l_e.*ones_t;

		end
		function plot(~,out)
			%% Plot simulation result.
			figure('Name','no_ribo');
			subplot(3,3,1);
			plot(out.t, out.no_ribo__nu);
			title("no_ribo__nu");
			ylim([0, +inf]);
			grid on;

			subplot(3,3,2);
			plot(out.t, out.no_ribo__mu);
			title("no_ribo__mu");
			ylim([0, +inf]);
			grid on;

			subplot(3,3,3);
			plot(out.t, out.no_ribo__r);
			title("no_ribo__r");
			ylim([0, +inf]);
			grid on;

			subplot(3,3,4);
			plot(out.t, out.no_ribo__m_h);
			title("no_ribo__m_h");
			ylim([0, +inf]);
			grid on;

			subplot(3,3,5);
			plot(out.t, out.no_ribo__J_host_sum);
			title("no_ribo__J_host_sum");
			ylim([0, +inf]);
			grid on;

			subplot(3,3,6);
			plot(out.t, out.no_ribo__K_C0);
			title("no_ribo__K_C0");
			ylim([0, +inf]);
			grid on;

			subplot(3,3,7);
			plot(out.t, out.no_ribo__E_m);
			title("no_ribo__E_m");
			ylim([0, +inf]);
			grid on;

			subplot(3,3,8);
			plot(out.t, out.no_ribo__J);
			title("no_ribo__J");
			ylim([0, +inf]);
			grid on;

			subplot(3,3,9);
			plot(out.t, out.no_ribo__m);
			title("no_ribo__m");
			ylim([0, +inf]);
			grid on;

		end
	end
end
